INSERT INTO `frutas_del_diablo` (`id`, `name`, `description`, `roman_name`, `type`, `filename`, `technicalFile`) VALUES
(152, 'Fruit of the Whisper', 'This fruit enables the user to understand the heart of an animal or, in other words, to speak to animals. The user cannot control the animal s will.', 'Hiso Hiso no Mi', 'Paramecia', 'https://images.api-onepiece.com/fruits/', 'https://images.api-onepiece.com/fruits/'),
(153, 'Fruit of the Slash-Slash', 'whoever has eaten this fruit can create blades of air as sharp as a sword.', 'Kama Kama no Mi', 'Paramecia', 'https://images.api-onepiece.com/fruits/', 'https://images.api-onepiece.com/fruits/'),
(154, 'Unbreakable Fruit', 'whoever eats it becomes as hard as rock, and can increase the heat of certain body parts so that his opponent burns himself by touching them.', 'Kachi Kachi no Mi', 'Paramecia', 'https://images.api-onepiece.com/fruits/', 'https://images.api-onepiece.com/fruits/'),
(155, 'Fruit of Sleep', 'This fruit can put anyone to sleep via hypnosis. The user can thus penetrate the memory of the sleeping person to inflict damage such as memory loss or nightmares.', 'Nemu Nemu no Mi', 'Paramecia', 'https://images.api-onepiece.com/fruits/', 'https://images.api-onepiece.com/fruits/'),
